// Include the V5 Library
#include "vex.h"
  
// Allows for easier use of the VEX Library
using namespace vex;

bool Goal=false;
bool test=false;

competition Competition;

void auton() {
  Drivetrain.driveFor(forward, 24, inches);
}

void goal(){
  if (Goal == false){
    Lever.set(true);
    Goal=true;
  } else {
    Lever.set(false);
    Goal=false;
  }
}
int main(){
  Drivetrain.setDriveVelocity(100, percent);
  Drivetrain.setTurnVelocity(100, percent);
  Donuts.setMaxTorque(100, percent);
  Donuts.setVelocity(100,percent);
  Controller1.ButtonA.pressed(goal);

  Competition.autonomous(auton);
}
